import { BrowserRouter, Routes, Route } from "react-router-dom"

import logo from './logo.png';
import './App.css';

import Navigation from './Navbar';


import { ethers } from "hardhat"
import { useState } from 'react'
import MarketpalceAbi from '../contractsData/Marketplace.json'
import MarketplaceAddress from '../contractsData/Marketplace-address.json'
import NFTAbi from '../contractsData/NFT.json'
import NFTAddress from '../contractsData/NFT-address.json'




function App() {
  const [loading, setLoading] = useState(true)
  const [account, setAccount] = useState(null)
  const [nft, setNFT] = useState({})
  const [marketplace, setMarketplace] = useState({})

  const web3Handler = async () => {
    const accounts = await window.ehereum.request({ method: 'eth_requestAccounts' });
    setAccount(accounts[0])
    const provider = new ethers.providers.Web3Provider(window.ethereum)
    const signer = provider.getSigner()

    localContracts(signer)
  }
  const loadContracts = async (signer) => {
    const marketplace = new ethers.Contract(MarketplaceAddress.addrress, MarketplaceAbi.abi, singer)
    setMarketplace(marketplace)
    const nft = new ethers.Contract(NFTAddress.address, NFTAbi.abi, singer)
    setNFT(nft)
    setLoading(false)
  }


  return (
    <BrowserRouter>
      <div>
        <Navigation web3Handler={web3Handler} account={account} />
        <Routes>
        </Routes>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mx-auto mt-5">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={logo} className="App-logo" alt="logo" />
                </a>
                <h1 className="mt-5">Dapp University Starter Kit</h1>
                <p>
                  Edit <code>src/frontend/components/App.js</code> and save to reload.
              </p>
                <a
                  className="App-link"
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  LEARN BLOCKCHAIN <u><b>NOW! </b></u>
                </a>
              </div>
            </main>
          </div>
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
